mapper.update:
update item_table3 
set
item_Name = #{item_Name}
,introduce = #{introduce}
,image = #{image}
,address = #{address}
,people = #{people}
,item_Url = #{item_Url}
where
item_Idx = #{item_Idx}

mapper.select:
select rownum rn, item_Idx, country, item_Name, regdate, introduce, image, address, item_Category, people, price, tel, item_Option, item_Url from (select rownum rn, item_Idx, country, item_Name, regdate, introduce, image, address, item_Category, people, price, tel, item_Option, item_Url from item_table3 where rownum <= (#{amount} * #{page})
and country = #{country} and item_Category = #{item_Category} and people >= #{people}
) where rn > (#{amount} * #{page} - #{amount})


select count(*) from item_table3 where 1=1 

and country = #{country}
and item_Category = #{item_Category}
and people >= #{people}
